class showView {

    _parentElement = document.querySelector('.movie-list');
    _data;


    render(data) {

        // if (!data || (Array.isArray(data) && data.length === 0)) {
        //     return this.renderError();
        // }

        // this._clear();
        data.map((element) => {

            const markup = this._generateMarkup(element);
            // this._clear();
            this._parentElement.insertAdjacentHTML('afterbegin', markup);

        })


    }


    _generateMarkup(element) {

        // `

        //     <div class="card"><img src="${element.image}" alt=""></div>

        //     `;
        return `                
        <div class="movie-details">
        <div class="card">
            <img src="${element.image == '' ? "/image/movie.jpg" : element.image}" alt="">
            <div class="hover-div">
                <div class="item">
                    <img src="image\active-my-list.png" alt="">
                    <img src="image\active-favourite.png" alt="">
                    <img src="image\active-watch-later.png" alt="">
                </div>
            </div>
        </div>
        <h5>${element.type}</h5>
        <h4>${element.name}</h4>
    </div>
        `

    }

    _clear() {
        this._parentElement.innerHTML = '';
    }

}



export default new showView();
